mapper.select:

		  select * 
		  from(
		  SELECT /*+INDEX_DESC(BOARD PK_BOARD)*/
          ROWNUM RN, BNO, TITLE, B_CONTENT, NICK_NAME, B_CLICKNUM, B_LIKE, REGDATE, UPDATEDATE 
          FROM BOARD
				 where
				 rownum <= #{pageNum} * #{amount}




							(b_Type = '2')


							(b_Type = '3')


							(b_Type = '4')


						   (b_Type = '2' and b_Type ='3' and b_Type='4')



							)
			where 	rn > (#{pageNum} - 1) * #{amount}

mapper.update:

update 	board
set		b_clickNum = (1 + (select b_clickNum from board where bno = #{bno}))
where 	bno = #{bno}


update  board
set		b_Like = (1 + (select b_Like from board where bno = #{bno}))
where 	bno = #{bno}
