mapper.select:

update 	board
set		b_clickNum = (1 + (select b_clickNum from board where bno = #{bno}))
where 	bno = #{bno}


update 	board
set		b_Like = (1 + (select b_Like from board where bno = #{bno}))
where 	bno = #{bno}

mapper.insert:
insert 
into board(
       b_type,btype_name,member_no, bno, title, b_content, nick_Name
          )
values (
       #{b_Type},#{bType_Name},#{member_No}, seq_board.nextval, #{title}, #{b_content}, #{nick_Name}
       )

 select seq_board.nextval from dual

insert 
into board(
         b_type,btype_name,member_no, bno, title, b_content, nick_Name
         )
values (
        #{b_Type},#{bType_Name},#{member_No}, seq_board.nextval, #{title}, #{b_content}, #{nick_Name}
        )

mapper.update:

update board
set title = #{title}, b_content = #{b_content}, nick_Name = #{nick_Name}, updateDate = sysdate
where bno = #{bno}
