mapper.select:

		  select * 
		  from(
		  SELECT /*+INDEX_DESC(BOARD PK_BOARD)*/
          ROWNUM RN, BNO, TITLE, B_CONTENT, NICK_NAME, B_CLICKNUM, B_LIKE, REGDATE, UPDATEDATE 
          FROM BOARD
				 where
				 rownum <= #{pageNum} * #{amount}



                   b_Type=#{b_Type}



							)
			where 	rn > (#{pageNum} - 1) * #{amount}

		select count(*) from board where





                 b_Type=#{b_Type}




			bno > 0

mapper.update:

update 	board
set		b_clickNum = (1 + (select b_clickNum from board where bno = #{bno}))
where 	bno = #{bno}

update board
set like_Check = like_Check +1
where memner_No=#{member_No) and bno=#{bno}

update board
set like_Check = 0
where memner_No=#{member_No} and bno=#{bno}
