mapper.select:

		  select * 
		  from(
		  SELECT /*+INDEX_DESC(BOARD PK_BOARD)*/
                 b_Type, bType_Name, BNO, TITLE, B_CONTENT, NICK_NAME, B_CLICKNUM, B_LIKE, REGDATE, UPDATEDATE
          FROM BOARD
				 where
				 member_No=#{member_No}
				 and
				 rownum <= #{pageNum} * #{amount}



				b_Type=#{b_Type}



							)
			where 	rn > (#{pageNum} - 1) * #{amount}


select	* 
from 	(select
		/*+ index_desc(planner pk_planner)*/
				rownum rn, plan_No, p_Title, city_Name, trip_Period, nick_Name, plan_Like, click_Num, p_Thumbnail
		from 	planner, city, memberinfo
		where	member_No=#{member_No}	
				and planner.email=memberinfo.email
				and planner.city_No=city.city_No 
				and rownum <= #{pageNum} * #{amount}



				)
where 	rn > (#{pageNum} - 1) * #{amount}


			select *
			from (select
					/*+ index_desc(planner pk_planner)*/
							rownum rn, planner.plan_No, p_Title, city_Name, trip_Period, nick_Name, plan_Like, click_Num, p_Thumbnail
					from 	planner , city
					where	planner.city_No=city.city_No 
							and rownum <= #{pageNum} * #{amount}
							and status = 1
    		) a, scrap
			where scrap.plan_No = a.plan_No 
				  and member_no=#{member_No}
				  and rn > (#{pageNum} - 1) * #{amount}


		  select * 
		  from(
		  		SELECT /*+INDEX_DESC(BOARD PK_BOARD)*/
                 bType_Name, a.BNO, TITLE, NICK_NAME, scrap_date
          		from board a, scrap b
				where a.bno=b.bno
				  and rownum <= #{pageNum} * #{amount}				 



							) 
			where member_No=#{member_No}
			  and rn > (#{pageNum} - 1) * #{amount}
