mapper.select:
select * from (select /*+ index_desc(planner pk_planner)*/ rownum rn, plan_No, p_Title, city_Name, trip_Period, nick_Name, plan_Like, click_Num from planner, city where
planner.city_No=city.city_No and rownum <= #{pageNum} * #{amount} and status = 1
order by plan_No desc
order by plan_Like desc
order by click_Num desc
) where rn > (#{pageNum} - 1) * #{amount}

mapper.update:

	update 	planner
	set		click_Num = (1 + (select click_Num from planner where plan_No = #{plan_No}))
	where 	plan_No = #{plan_No}

mapper.select:

	select 	count(*)
	from	planner_like
	where 	plan_No = #{plan_No} and email = #{email}

mapper.update:

	update 	planner
	set		plan_Like = (1 + (select plan_Like from planner where plan_No = #{plan_No}))
	where 	plan_No = #{plan_No}

mapper.select:

	select 	plan_No, p_Title, nick_Name, city_No, trip_Period, plan_Like, p_Content, updateDate
	from 	planner
	where 	email = #{email}
