mapper.insert:
	   
			select seq_reply.nextval from dual
		
  insert 
  into reply(
         rno,b_type, bno,r_content, nick_name, regdate, updatedate,email
           )
  values (
         seq_reply.nextval,#{b_Type},#{bno},#{r_Content}, #{nick_Name}, #{regdate},#{updatedate},#{email}
         )
